const transformDataStackedBar = (data) => {
  const result = []

  // Iterar sobre cada chave (1, 2, 3, etc. representando os "users")
  Object.keys(data).forEach((user) => {
    const events = data[user]

    // Criar um objeto para contar os tipos de evento para cada "user"
    const typeCounts = {}

    events.forEach((event) => {
      const eventType = event.Type // Obter o tipo de evento (INPUT/OUTPUT)

      // Inicializar ou incrementar o contador para o tipo específico
      if (typeCounts[eventType]) {
        typeCounts[eventType]++
      } else {
        typeCounts[eventType] = 1
      }
    })

    // Converter os contadores de tipos em objetos formatados para o resultado
    Object.keys(typeCounts).forEach((type) => {
      result.push({
        name: user, // O número do "user"
        type: type, // O tipo (INPUT/OUTPUT)
        count: typeCounts[type], // A contagem de eventos desse tipo
      })
    })
  })

  return result
}

export default transformDataStackedBar
