import React, { useEffect, useState } from 'react'
import { fetchStaticCountWordsFromAPI } from '../../../api/StaticData'
import WordCloud from '../../charts/WordCloud'

const StaticWordCloud = ({ data }) => {
  const [boxPlotData, setBoxPlotData] = useState([])
  const [dataLoaded, setDataLoaded] = useState(false)

  useEffect(() => {
    const fetchWordCloud = async () => {
      try {
        let fetchedBoxPlotData = []
        if (data) {
          fetchedBoxPlotData = data
        } else {
          fetchedBoxPlotData = await fetchStaticCountWordsFromAPI()
        }
        setBoxPlotData(fetchedBoxPlotData)
        setDataLoaded(true)
      } catch (error) {
        console.error('Error fetching word cloud data:', error)
      }
    }

    fetchWordCloud().catch((error) => console.error(error))
  }, [data])

  return dataLoaded && <WordCloud data={boxPlotData} />
}

export default StaticWordCloud
