import React, { useState, useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import './App.css';
import BarChart from './components/charts/BarChart';

function App() {
  const [data, setData] = useState(null); // Usando o estado para armazenar os dados

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await fetchStaticDataFromAPI();
        setData(fetchedData); // Define os dados no estado
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  console.log('dados:', data);

  return (
    <div>
      <h1>Bar Chart</h1>
      {data && <BarChart data={data} />} {/* Renderiza o gráfico somente quando os dados estiverem disponíveis */}
    </div>
  );
}

export default App;
