import React, { useEffect, useState } from 'react';
import RealTimeBarChart from './charts/RealTimeBarChart';
import KafkaDataReceiver from '../../api/OnlineData';
import CountRealTimeEvents from './CountRealTimeEvents';

const RealTimeData = () => {
  const [realTimeData, setRealTimeData] = useState([]);
  const [eventCounts, setEventCounts] = useState({});

  useEffect(() => {
    const onDataReceived = (receivedData) => {
      // Atualiza o estado dos contadores de eventos com base nos dados recebidos
      const newEventCounts = CountRealTimeEvents(receivedData, { ...eventCounts });
      setEventCounts(newEventCounts);

      // Atualiza os dados do gr치fico com base nos contadores de eventos atualizados
      const newData = Object.entries(newEventCounts).map(([eventType, count]) => ({
        name: eventType,
        value: count,
      }));

      // Adiciona os novos dados aos dados existentes do gr치fico
      setRealTimeEventCounts(prevCounts => CountRealTimeEvents(receivedData, prevCounts));
      console.log('Event counts:', newEventCounts);
    };

    KafkaDataReceiver(onDataReceived);
  }, []);

  return (
    <React.Fragment>
      <div>
        <h1>Real-Time Bar Chart</h1>
        <RealTimeBarChart data={realTimeData} />
      </div>
    </React.Fragment>
  );
};

export default RealTimeData;
