import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import {
  Alert,
  Button,
  Container,
  Dropdown,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  ListGroup,
  ListGroupItem,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Tooltip,
} from 'reactstrap'
import {
  fetchStaticAllFilesFromAPI,
  fetchStaticFilteredDataFromAPI,
} from '../../api/StaticData'

const MainPage = () => {
  const navigate = useNavigate()
  const [dataset, setDataset] = useState([])
  const [saveDataset, setSaveDataset] = useState([])
  const [showUsersList, setShowUsersList] = useState(false)
  const [allUsers, setAllUsers] = useState([])
  const [checkedUsers, setCheckedUsers] = useState([])
  const [filteredUsers, setFilteredUsers] = useState([])
  const [modal, setModal] = useState(false)
  const [showToast, setShowToast] = useState(false)
  const [showNoUsers, setShowNoUsers] = useState(false)
  const [dropdownOpen, setDropdownOpen] = useState(false)
  const [tooltipOpen, setTooltipOpen] = useState({})

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const formatDatasetName = (filename) => {
    const nameWithoutExtension = filename.replace('.json', '')
    return nameWithoutExtension
      .replace(/^dataset/i, 'Dataset')
      .replace(/(\d+)/, ' $1')
  }

  const handleDatasetClick = async (datasetName) => {
    try {
      const fetchedUsersData = await fetchStaticAllFilesFromAPI(datasetName)
      if (fetchedUsersData.length === 0) {
        setShowNoUsers(true)
        return
      }
      setAllUsers(fetchedUsersData || [])
      setSaveDataset(datasetName)
      setShowUsersList(true)
      setCheckedUsers([])
      setFilteredUsers([])
      setModal(!modal)
      setTooltipOpen(false)
    } catch (error) {
      console.error('Error fetching Users Data:', error)
    }
  }

  const tooltipData = (file) => [
    {
      label: 'Name',
      value: file.dataset !== null ? file.dataset : 'No dataset Name',
    },
    {
      label: 'Number of Users',
      value: file.users !== null ? file.users : '0',
    },
    {
      label: 'Dataset Time',
      value: file.total_duration !== null ? `${file.total_duration} s` : '0 s',
    },
    {
      label: 'Number of Types',
      value: file.type_counts ? file.type_counts : 'No data',
    },
    {
      label: 'Number of Event Types',
      value: file.event_type_counts ? file.event_type_counts : 'No data',
    },
  ]

  useEffect(() => {
    if (showNoUsers || showToast) {
      const timer = setTimeout(() => {
        setShowNoUsers(false)
        setShowToast(false)
      }, 2000)
      return () => clearTimeout(timer)
    }
  }, [showNoUsers, showToast])

  const handleBackClick = () => {
    setShowUsersList(false)
    setModal(false)
    setAllUsers([])
    setCheckedUsers([])
    setFilteredUsers([])
  }

  const handleSubmitClick = async (datasetName) => {
    if (filteredUsers.length === 0) {
      setShowToast(true)
      return
    }
    try {
      const fetchedFilteredData = await fetchStaticFilteredDataFromAPI(
        datasetName,
        filteredUsers
      )
      const updatedData = fetchedFilteredData || []

      navigate('/overview', {
        state: {
          receivedData: updatedData,
          dataset: saveDataset,
          totalUsers: allUsers,
          selectedUsers: checkedUsers,
        },
      })

      setShowUsersList(false)
    } catch (error) {
      console.error('Error fetching Filtered data:', error)
    }
  }

  const handleCheckboxChange = (user) => {
    setCheckedUsers((prevCheckedUsers) => {
      const isChecked = prevCheckedUsers.includes(user)
      const updatedUsers = isChecked
        ? prevCheckedUsers.filter((u) => u !== user)
        : [...prevCheckedUsers, user]
      transformUsers(updatedUsers)
      return updatedUsers
    })
  }

  const handleSelectAllUsers = (allUsers) => {
    transformUsers(allUsers)
    setCheckedUsers(allUsers)
  }

  const transformUsers = (checkedUsers) => {
    const transformedUsers = checkedUsers.map((userObj) => {
      const userName = userObj.user
      return userName.replace(/\D/g, '')
    })
    setFilteredUsers(transformedUsers)
  }

  const sortedDataset = dataset.sort((first, second) =>
    first.dataset.localeCompare(second.dataset)
  )

  const toggleDropdown = () => setDropdownOpen((prevState) => !prevState)

  const toggleTooltip = (index) => {
    setTooltipOpen((prev) => ({ ...prev, [index]: !prev[index] }))
  }

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>

      {/* Grid com três secções */}
      <div className="grid-container">
        {/* Primeira secção com a lista */}
        <div className="grid-item">
          <Container>
            <ListGroup>
              <ListGroupItem action tag="button">
                Cras justo odio
              </ListGroupItem>
              <ListGroupItem action tag="button">
                Dapibus ac facilisis in
              </ListGroupItem>
              <ListGroupItem action tag="button">
                Morbi leo risus
              </ListGroupItem>
              <ListGroupItem action tag="button">
                Porta ac consectetur ac
              </ListGroupItem>
              <ListGroupItem action disabled tag="button">
                Vestibulum at eros
              </ListGroupItem>
            </ListGroup>
          </Container>
        </div>

        {0 && (
          <div className="grid-item">
            <p>Content for section 2</p>
          </div>
        )}

        {0 && (
          <div className="grid-item">
            <p>Content for section 3</p>
          </div>
        )}
      </div>
    </div>
  )
}

export default MainPage
