import React, { useEffect, useState } from 'react'
import fetchStaticDataFromAPI from '../../api/StaticData'
import CountEvents from './CountEvents'
import EventDuration from './EventDuration'
import StaticBarChart from './charts/StaticBarChart'
import ScatterPlot from './charts/ScatterPlot'
import LineChart from './charts/LineChart' // Importe o componente LineChart aqui
import ConvertDataForScatterPlot from './utils/ConvertDataForScatterPlot'
import ConvertDataForLineChart from './utils/ConvertDataForLineChart' // Importe a função para converter dados para o LineChart

const StaticData = () => {
  const [staticData, setStaticData] = useState([])

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedStaticData = await fetchStaticDataFromAPI()
        setStaticData(fetchedStaticData)
      } catch (error) {
        console.error('Error fetching Static Data:', error)
      }
    }

    fetchStaticData()
  }, [])

  // Calcula contagens de eventos e duração de eventos usando os dados estáticos
  const eventDataCounts = CountEvents(staticData)
  const eventDurations = EventDuration(staticData)
  const scatterPlotData = ConvertDataForScatterPlot(eventDurations)
  const eventStaticDataForChart = Object.entries(eventDataCounts).map(
    ([eventType, count]) => ({
      name: eventType,
      value: count,
    })
  )
  const lineChartData = ConvertDataForLineChart(eventDurations)

  return (
    <React.Fragment>
      <div>
        <h1>Static Bar Chart</h1>
        <StaticBarChart data={eventStaticDataForChart} />
        <h1>Static Scatter Chart</h1>
        <ScatterPlot data={scatterPlotData} />
        <h1>Static Line Chart</h1>
        {/* Renderize o componente LineChart com os dados necessários */}
        <LineChart data={lineChartData} />
      </div>
    </React.Fragment>
  )
}

export default StaticData
