import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const TimelineChart = ({ data }) => {
  const svgRef = useRef();

  useEffect(() => {
    if (!data) return;

    const margin = { top: 20, right: 30, bottom: 30, left: 40 };
    const width = 600 - margin.left - margin.right;
    const height = 200 - margin.top - margin.bottom;

    const svg = d3
      .select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const parseTime = d3.timeParse('%Y-%m-%d %H:%M:%S.%L');

    const x = d3.scaleTime().range([0, width]);

    const y = d3.scaleBand().range([height, 0]);

    const xAxis = d3.axisBottom(x);
    const yAxis = d3.axisLeft(y);

    x.domain([
      d3.min(data, (d) => parseTime(d[0].Start)),
      d3.max(data, (d) => parseTime(d[d.length - 1].End)),
    ]);

    y.domain(data.map((d, i) => i)).padding(0.1);

    svg.append('g').attr('class', 'x-axis').attr('transform', `translate(0,${height})`).call(xAxis);

    svg.append('g').attr('class', 'y-axis').call(yAxis);

    svg
      .selectAll('.bar')
      .data(data)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', (d) => x(parseTime(d[0].Start)))
      .attr('y', (d, i) => y(i))
      .attr('width', (d) => x(parseTime(d[d.length - 1].End)) - x(parseTime(d[0].Start)))
      .attr('height', y.bandwidth())
      .attr('fill', 'steelblue');
  }, [data]);

  return <svg ref={svgRef}></svg>;
};

export default TimelineChart;
