import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../styles.css'
import {
  Alert,
  Button,
  Container,
  Nav,
  NavDropdown,
  Tooltip,
  OverlayTrigger,
} from 'react-bootstrap'
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap'
import {
  fetchStaticAllFilesFromAPI,
  fetchStaticFilteredDataFromAPI,
} from '../../api/StaticData'

const MainPage = () => {
  const navigate = useNavigate()
  const [dataset, setDataset] = useState([])
  const [saveDataset, setSaveDataset] = useState([])
  const [showUsersList, setShowUsersList] = useState(false)
  const [selectedUsers, setSelectedUsers] = useState([])
  const [checkedUsers, setCheckedUsers] = useState([])
  const [filteredUsers, setFilteredUsers] = useState([])
  const [modal, setModal] = useState(false)
  const [showToast, setShowToast] = useState(false)
  const [showNoUsers, setShowNoUsers] = useState(false)

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const handleDatasetClick = async (datasetName) => {
    try {
      const fetchedUsersData = await fetchStaticAllFilesFromAPI(datasetName)
      if (fetchedUsersData.length === 0) {
        setShowNoUsers(true)
        return
      }
      setSelectedUsers(fetchedUsersData || [])
      setSaveDataset(datasetName)
      setShowUsersList(true)
      setCheckedUsers([])
      setFilteredUsers([])
      setModal(!modal)
    } catch (error) {
      console.error('Error fetching Users Data:', error)
    }
  }

  useEffect(() => {
    if (showNoUsers || showToast) {
      const timer = setTimeout(() => {
        setShowNoUsers(false)
        setShowToast(false)
      }, 2000)
      return () => clearTimeout(timer)
    }
  }, [showNoUsers, showToast])

  const handleBackClick = () => {
    setShowUsersList(false)
    setModal(false)
    setSelectedUsers([])
    setCheckedUsers([])
    setFilteredUsers([])
  }

  const handleSubmitClick = async (datasetName) => {
    if (filteredUsers.length === 0) {
      setShowToast(true)
      return
    }
    try {
      const fetchedFilteredData = await fetchStaticFilteredDataFromAPI(
        datasetName,
        filteredUsers
      )
      const updatedData = fetchedFilteredData || []

      navigate('/overview', {
        state: {
          receivedData: updatedData,
          dataset: saveDataset,
          totalUsers: selectedUsers,
          selectedUsers: checkedUsers,
        },
      })

      setShowUsersList(false)
    } catch (error) {
      console.error('Error fetching Filtered data:', error)
    }
  }

  const handleCheckboxChange = (user) => {
    setCheckedUsers((prevCheckedUsers) => {
      const isChecked = prevCheckedUsers.includes(user)
      const updatedUsers = isChecked
        ? prevCheckedUsers.filter((u) => u !== user)
        : [...prevCheckedUsers, user]
      transformUsers(updatedUsers)
      return updatedUsers
    })
  }

  const handleSelectAllUsers = (users) => {
    transformUsers(selectedUsers)
    setCheckedUsers(selectedUsers)
  }

  const transformUsers = (checkedUsers) => {
    const transformedUsers = checkedUsers.map((userObj) => {
      const userName = userObj.user
      return userName.replace(/\D/g, '')
    })
    setFilteredUsers(transformedUsers)
  }

  const sortedDataset = dataset.sort((first, second) =>
    first.dataset.localeCompare(second.dataset)
  )

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown
              title="Choose your Dataset"
              id="basic-nav-dropdown-categories"
            >
              {Object(sortedDataset).map((file, index) => (
                <OverlayTrigger
                  key={index}
                  placement="right"
                  overlay={
                    <Tooltip
                      id={`tooltip-${index}`}
                    >{`Users: ${file.users !== null ? file.users : 0}`}</Tooltip>
                  }
                >
                  <NavDropdown.Item
                    onClick={() => handleDatasetClick(file.dataset)}
                  >
                    <div>
                      <strong>{file.dataset}</strong>
                      <div style={{ fontSize: 'small', color: 'gray' }}>
                        Users: {file.users !== null ? file.users : 0}
                      </div>
                    </div>
                  </NavDropdown.Item>
                </OverlayTrigger>
              ))}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
      <Modal isOpen={modal}>
        <div className="modalStyle">
          <ModalHeader className="modalHeader">List of Users</ModalHeader>
          {showToast && (
            <div>
              {['warning'].map((variant) => (
                <Alert key={variant} variant={variant}>
                  Please select at least one user!
                </Alert>
              ))}
            </div>
          )}
          <ModalBody>
            {showUsersList && (
              <div className="usersList">
                <ul>
                  {selectedUsers.map((user, index) => (
                    <li key={index} className="userItem">
                      <input
                        type="checkbox"
                        checked={checkedUsers.includes(user)}
                        onChange={() => handleCheckboxChange(user)}
                        className="checkboxList"
                      />
                      {user.user}
                      <div className="userSettings">
                        Duration: {user.duration} seconds
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </ModalBody>
          <ModalFooter
            style={{ display: 'flex', justifyContent: 'space-between' }}
          >
            {selectedUsers.length > 0 && (
              <React.Fragment>
                <div>
                  <Button
                    style={{ backgroundColor: '#506266', border: 'none' }}
                    color="secondary"
                    onClick={() => handleBackClick()}
                  >
                    Cancel
                  </Button>
                  <Button
                    style={{ backgroundColor: '#506266', border: 'none' }}
                    className="selectButton"
                    onClick={() => handleSelectAllUsers(selectedUsers)}
                    color="primary"
                  >
                    Select All
                  </Button>
                </div>
                <div>
                  <Button
                    onClick={() => handleSubmitClick(saveDataset)}
                    color="secondary"
                  >
                    Submit
                  </Button>
                </div>
              </React.Fragment>
            )}
          </ModalFooter>
        </div>
      </Modal>
      {showNoUsers && (
        <div>
          {['warning'].map((variant) => (
            <Alert key={variant} variant={variant}>
              No information found!
            </Alert>
          ))}
        </div>
      )}
    </div>
  )
}

export default MainPage
