import React, { useEffect, useRef, useState } from 'react'
import * as d3 from 'd3'
import './../styles.css' // Importe o CSS para a tooltip

const PieChart = ({ data }) => {
  const chartRef = useRef()
  const [tooltip, setTooltip] = useState({
    visible: false,
    content: '',
    x: 0,
    y: 0,
  })

  useEffect(() => {
    d3.select(chartRef.current).selectAll('*').remove()

    const width = 400
    const height = 400
    const radius = Math.min(width, height) / 2 - 50

    const color = d3.scaleOrdinal(d3.schemeCategory10)

    const svg = d3
      .select(chartRef.current)
      .append('svg')
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2},${height / 2})`)

    const partition = d3.partition().size([2 * Math.PI, radius])
    const root = d3.hierarchy(data).sum((d) => d.value)
    partition(root)

    const arc = d3
      .arc()
      .startAngle((d) => d.x0)
      .endAngle((d) => d.x1)
      .innerRadius((d) => d.y0)
      .outerRadius((d) => d.y1)

    svg
      .selectAll('path')
      .data(root.descendants().slice(1))
      .enter()
      .append('path')
      .attr('d', arc)
      .style('stroke', '#fff')
      .style('fill', (d) => color(d.data.name))
      .on('mouseover', (event, d) => {
        let tooltipText = ''
        if (d.depth === 1) {
          if (d.data.type) {
            tooltipText += `Type: ${d.data.type}<br/>`
          }
          if (d.data.name) {
            tooltipText += `Event Type: ${d.data.name}<br/>`
          }
          if (d.value) {
            tooltipText += `Count: ${d.value}`
          }
        } else if (d.depth === 2) {
          if (d.data.name) {
            tooltipText += `Semantic: ${d.data.name}<br/>`
          }
          if (d.value) {
            tooltipText += `Count: ${d.value}`
          }
        }

        setTooltip({
          visible: true,
          content: tooltipText,
          x: event.pageX,
          y: event.pageY,
        })
      })
      .on('mousemove', (event) => {
        setTooltip((prev) => ({ ...prev, x: event.pageX, y: event.pageY }))
      })
      .on('mouseout', () => {
        setTooltip({ ...tooltip, visible: false })
      })
  }, [data])

  return (
    <div>
      <div ref={chartRef}></div>
      {tooltip.visible && (
        <div
          className="tooltip"
          style={{
            left: tooltip.x + 10,
            top: tooltip.y - 30,
            position: 'absolute',
            pointerEvents: 'none', // Para que a tooltip não atrapalhe o mouse
            zIndex: 10, // Para garantir que a tooltip esteja acima do SVG
          }}
          dangerouslySetInnerHTML={{ __html: tooltip.content }}
        />
      )}
    </div>
  )
}

export default PieChart
