import React, { useEffect, useState } from 'react'
import BubbleChart from './charts/BubbleChart'
import EventDuration from './utils/EventDuration'
import {
  fetchAllStaticDataFromAPI,
  fetchStaticPiesFromAPI,
} from '../../api/StaticData'

const BubbleChartPage = () => {
  const [staticData, setStaticData] = useState([])
  const [pieData, setPieData] = useState([])

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedStaticData = await fetchAllStaticDataFromAPI()
        setStaticData(fetchedStaticData)
      } catch (error) {}
    }

    fetchStaticData()
  }, [])

  useEffect(() => {
    const fetchPieData = async () => {
      try {
        const fetchedData = await fetchStaticPiesFromAPI()
        setPieData(fetchedData)
      } catch (error) {
        console.error('Error fetching pie chart data:', error)
      }
    }

    fetchPieData()
  }, [])

  const eventDurations = EventDuration(staticData)

  return (
    <React.Fragment>
      <div className="pages-container">
        <div className="pages-text">
          <h1>Bubble Chart</h1>
          <BubbleChart data={eventDurations} />
        </div>
        <div className="pages-text">
          <h1>Pie Chart</h1>
          <BubbleChart data={eventDurations} />
        </div>
      </div>
    </React.Fragment>
  )
}

export default BubbleChartPage
