// StaticData.js
import fetchStaticDataFromAPI from './api/StaticData';
import React, { useEffect, useState } from 'react';
import countEvents from './countEvents';
import { calculateEventDurations, convertDataForScatterPlot } from './eventDuration';
import BarChart from '../charts/BarChart';

const StaticData = () => {
  const [staticData, setStaticData] = useState([]);

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedStaticData = await fetchStaticDataFromAPI();
        setStaticData(fetchedStaticData);
      } catch (error) {
        console.error('Error fetching Static Data:', error);
      }
    };

    fetchStaticData();
  }, []);

  const eventDataCounts = countEvents(staticData);
  const eventDurations = calculateEventDurations(staticData);
  const scatterPlotData = convertDataForScatterPlot(eventDurations);

  const eventStaticDataForChart = Object.entries(eventDataCounts).map(([eventType, count]) => ({
    name: eventType,
    value: count,
  }));

  return (
    <React.Fragment>
      <div>
        <h1>Bar Chart</h1>
        <BarChart data={eventStaticDataForChart} />
        <h1>Scatter Chart</h1>
        <scatterPlotData data={scatterPlotData} />
      </div>
    </React.Fragment>
  );
};

export default StaticData;
