import React, { useState } from 'react'
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap'
import StaticSunBurst from '../buildCharts/sunBurstChart/StaticSunBurst'

const ChooseCharts = ({ title }) => {
  const [activeChart, setActiveChart] = useState('BarChart')
  const [boxPlotData, setBoxPlotData] = useState({})
  const [wordCloudData, setWordCloudData] = useState({})
  const [timeLineData, setTimeLineData] = useState({})
  const [sunburstData, setSunburstData] = useState({})
  const [sunburstLabel, setSunburstLabel] = useState({})
  const [eventTypeColor, setEventTypeColor] = useState({})
  const [expData, setExpData] = useState([])

  const handleDataReceived = (data) => {
    setBoxPlotData(data)
  }

  const handleWordDataReceived = (data) => {
    setWordCloudData(data)
  }

  const handleTimeLineDataReceived = (data) => {
    setTimeLineData(data)
  }

  const handelEventTypeColor = (data) => {
    setEventTypeColor(data)
  }

  const handelSunburstDataReceived = (data) => {
    setSunburstData(data)
  }

  const handelSunburstLabelReceived = (data) => {
    setSunburstLabel(data)
  }

  const renderActiveChart = () => {
    switch (activeChart) {
      case 'SunBurst':
        return sunburstLabel ? (
          <StaticSunBurst
            timeLabel={sunburstLabel}
            data={sunburstData}
            onDataReceived={handleDataReceived}
            onWordDataReceived={handleWordDataReceived}
            onTimeLineDataReceived={handleTimeLineDataReceived}
            onEventTypeColor={handelEventTypeColor}
          />
        ) : (
          <StaticSunBurst
            onDataReceived={handleDataReceived}
            onWordDataReceived={handleWordDataReceived}
            onTimeLineDataReceived={handleTimeLineDataReceived}
            onEventTypeColor={handelEventTypeColor}
          />
        )
      case 'LineChart':
        return <LineChart />
      case 'PieChart':
        return <PieChart />
      default:
        return null
    }
  }
  return (
    <Navbar expand="lg" className={'custom-navbar'}>
      <Container fluid>
        <Navbar.Brand href="#home">{title}</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-dark-example" />
        <Navbar.Collapse id="navbar-dark-example">
          <Nav className="ms-auto">
            <NavDropdown
              id="nav-dropdown-dark-example"
              title="Choose a Chart"
              onSelect={(eventKey) => setActiveChart(eventKey)}
            >
              <NavDropdown.Item eventKey="SunBurst">SunBurst</NavDropdown.Item>
              <NavDropdown.Item eventKey="LineChart">
                Line Chart
              </NavDropdown.Item>
              <NavDropdown.Item eventKey="PieChart">Pie Chart</NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  )
}

export default ChooseCharts
