import React, { useEffect, useRef, useState } from 'react'
import { Alert, Button, Container, ListGroup, ListGroupItem } from 'reactstrap'
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const [dataset, setDataset] = useState([])
  const [selectedDataset, setSelectedDataset] = useState(null)
  const [focusIndex, setFocusIndex] = useState(1)
  const [allUsers, setAllUsers] = useState([])
  const [checkedUsers, setCheckedUsers] = useState([])
  const [disable, setDisable] = useState(false)
  const listRef = useRef([])
  const [submitError, setSubmitError] = useState(false)

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
        setFocusIndex(0)
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const formatDatasetName = (filename) => {
    const nameWithoutExtension = filename.replace('.json', '')
    return nameWithoutExtension
      .replace(/^dataset/i, 'Dataset')
      .replace(/(\d+)/, ' $1')
  }

  const updateSelectedDataset = (index) => {
    if (dataset[index]) {
      setSelectedDataset(dataset[index])
    }
  }

  const handleDatasetClick = async (dataset, index) => {
    setSelectedDataset(dataset)
    setFocusIndex(index)

    try {
      const fetchedUsersData = await fetchStaticAllFilesFromAPI(dataset.dataset)
      setAllUsers(fetchedUsersData || [])
      setCheckedUsers([])
    } catch (error) {
      console.error('Error fetching users:', error)
    }
  }

  const handleKeyDown = (event) => {
    if (dataset.length === 0) return

    let newIndex = focusIndex

    switch (event.key) {
      case 'ArrowUp':
        newIndex = (focusIndex - 1 + dataset.length) % dataset.length
        //handleDatasetClick(dataset[focusIndex], focusIndex - 1)
        console.log('NewIndex1:', newIndex)
        break
      case 'ArrowDown':
        newIndex = (focusIndex + 1) % dataset.length
        console.log('NewIndex2:', newIndex)
        //handleDatasetClick(dataset[focusIndex], focusIndex - 1)
        break
      default:
        return
    }

    console.log('NewIndex:', newIndex)

    setFocusIndex(newIndex)
    console.log('FocusIndex:', focusIndex)
    updateSelectedDataset(newIndex)

    if (listRef.current[newIndex]) {
      listRef.current[newIndex].focus()
    }
  }

  const handleCheckboxChange = (user) => {
    setCheckedUsers((prevCheckedUsers) => {
      const isChecked = prevCheckedUsers.some(
        (checkedUser) => checkedUser.user === user.user
      )
      const updatedUsers = isChecked
        ? prevCheckedUsers.filter((u) => u.user !== user.user)
        : [...prevCheckedUsers, user]

      transformUsers(updatedUsers)
      return updatedUsers
    })
  }

  const handleSelectAllUsers = (allUsers) => {
    console.log('allUsers:', allUsers)
    setCheckedUsers([...allUsers])
    setDisable(true)
    // transformUsers(allUsers)
  }

  const handleDisableAllUsers = () => {
    setDisable(false)
    setCheckedUsers([])
  }

  const transformUsers = (checkedUsers) => {
    if (Array.isArray(checkedUsers)) {
      const transformedUsers = checkedUsers.map((userObj) => {
        const userName = userObj.user // Certifique-se de que essa propriedade existe
        return userName.replace(/\D/g, '') // Remove caracteres não numéricos
      })
      setFilteredUsers(transformedUsers)
    } else {
      console.error('checkedUsers não é um array', checkedUsers)
    }
  }

  const handleSubmitClick = () => {
    if (checkedUsers.length === 0) {
      setSubmitError(true)
      console.log('Entra')
      return
    }
    console.log('Selected users:', checkedUsers)
  }

  const datasetFile = (file) => [
    {
      label: 'Name',
      value: file.dataset ? file.dataset : 'No dataset Name',
    },
    {
      label: 'Number of Users',
      value: file.users ? file.users : '0',
    },
    {
      label: 'Dataset Time',
      value: file.total_duration ? `${file.total_duration} s` : '0 s',
    },
    {
      label: 'Number of Types',
      value: file.type_counts ? formatObject(file.type_counts) : 'No data',
    },
    {
      label: 'Number of Event Types',
      value: file.event_type_counts
        ? formatObject(file.event_type_counts)
        : 'No data',
    },
  ]

  const formatObject = (obj) => {
    return Object.entries(obj)
      .map(([key, value]) => `${key}: ${value}`)
      .join('\n')
  }

  const sortedDataset = dataset.sort((first, second) =>
    first.dataset.localeCompare(second.dataset)
  )

  return (
    <div className="main-container" tabIndex={0} onKeyDown={handleKeyDown}>
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>

      <div className="grid-container">
        <div className="grid-item">
          <Container>
            <ListGroup>
              {sortedDataset.map((file, index) => (
                <ListGroupItem
                  key={index}
                  action
                  tag="button"
                  onClick={() => handleDatasetClick(file, index)}
                  ref={(el) => (listRef.current[index] = el)}
                  className={`dataset-list-item ${focusIndex === index ? 'focused' : ''}`}
                >
                  {formatDatasetName(file.dataset)}
                </ListGroupItem>
              ))}
            </ListGroup>
          </Container>
        </div>

        <div className="grid-item">
          {selectedDataset ? (
            <Container className="centered-container">
              <ListGroup className="scrollable-list">
                {datasetFile(selectedDataset).map((info, index) => (
                  <ListGroupItem key={index}>
                    <strong>{info.label}:</strong>
                    <pre
                      className="text-muted"
                      style={{ whiteSpace: 'pre-wrap', margin: '0' }}
                    >
                      {info.value}
                    </pre>
                  </ListGroupItem>
                ))}
              </ListGroup>
            </Container>
          ) : (
            <p>Select a dataset to view its details</p>
          )}
        </div>

        <div className="grid-item">
          {allUsers.length > 0 ? (
            <Container className="centered-container">
              <ListGroup className="scrollable-list">
                {allUsers.map((user, index) => (
                  <ListGroupItem key={index} className="user-item">
                    <div className="user-info">
                      <input
                        type="checkbox"
                        checked={checkedUsers.includes(user)}
                        onChange={() => handleCheckboxChange(user)}
                      />
                      <div className="user-details">
                        <div className="user-name">{user.user}</div>
                        <div className="user-duration">
                          Duration: {user.duration} seconds
                        </div>
                      </div>
                    </div>
                  </ListGroupItem>
                ))}
              </ListGroup>
              <div className="buttons-container">
                <div className="buttons-selected-container">
                  <div className="selectAll-container">
                    <Button
                      color="secondary"
                      onClick={() => handleSelectAllUsers(allUsers)}
                    >
                      Select All
                    </Button>
                  </div>
                  {disable && (
                    <div className="disableAll-container">
                      <Button color="secondary" onClick={handleDisableAllUsers}>
                        Disable
                      </Button>
                    </div>
                  )}
                </div>
                <div className="submit-container">
                  <Button color="primary" onClick={handleSubmitClick}>
                    Submit
                  </Button>
                </div>
              </div>
            </Container>
          ) : (
            <p>No users available</p>
          )}
        </div>
      </div>
      {submitError && (
        <Alert color="danger">You have to select at least one user</Alert>
      )}
    </div>
  )
}

export default MainPage
