import React, { useState, useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import KafkaDataReceiver from './api/OnlineData';

import './App.css';
import BarChart from './components/charts/staticData/BarChart';
import ScatterPlot from './components/charts/staticData/ScatterPlot';

function App() {
  const [staticData, setStaticData] = useState([]);
  const [onlineData, setOnlineData] = useState([]);

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedStaticData = await fetchStaticDataFromAPI();
        setStaticData(fetchedStaticData);
      } catch (error) {
        console.error('Error fetching Static Data:', error);
      }
    };

    fetchStaticData();
  }, []);

  console.log("Static Data: ", staticData)

  useEffect(() => {
    const onDataReceived = (receivedData) => {
      setOnlineData(receivedData);
    };

    KafkaDataReceiver(onDataReceived);
  }, []);

  console.log("Online Data: ", onlineData)

  // Função para contar o número de eventos em cada array 
  const countEvents = (dataArray) => {
    const eventCounts = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        if (eventType in eventCounts) {
          eventCounts[eventType]++;
        } else {
          eventCounts[eventType] = 1;
        }
      });
    });

    return eventCounts;
  };

  const calculateEventDurations = (dataArray) => {
    const eventDurations = {};

    dataArray.forEach((eventsArray) => {
      eventsArray.forEach((event) => {
        const eventType = event.EventType;
        const startTimestamp = new Date(event.Start);
        const endTimestamp = new Date(event.End);
        const duration = endTimestamp - startTimestamp;

        if (eventType in eventDurations) {
          eventDurations[eventType].push(duration);
        } else {
          eventDurations[eventType] = [duration];
        }
      });
    });

    return eventDurations;
  };

  const convertDataForScatterPlot = (time) => {
    const convertedData = [];

    for (const eventType in time) {
      const durations = time[eventType];
      const averageDuration = durations.reduce((acc, curr) => acc + curr, 0) / durations.length;

      // Atribuir a mesma coordenada x para todos os pontos de um tipo de evento
      const xCoordinate = eventType;

      // Usar a média das durações como coordenada y
      const yCoordinate = averageDuration;

      // Adicionar o ponto ao array convertido
      convertedData.push({ x: xCoordinate, y: yCoordinate });
    }

    return convertedData;
  };

  const eventDataCounts = countEvents(staticData);
  const eventDurations = calculateEventDurations(staticData);
  const scatterPlotData = convertDataForScatterPlot(eventDurations);

  const eventStaticDataForChart = Object.entries(eventDataCounts).map(([eventType, count]) => ({
    name: eventType,
    value: count,
  }));

  const eventOnlineDataCounts = countEvents(onlineData);
  const eventOnlineDurations = calculateEventDurations(onlineData);
  const scatterOnlinePlotData = convertDataForScatterPlot(eventOnlineDurations);

  const eventOnlineDataForChart = Object.entries(eventOnlineDataCounts).map(([eventType, count]) => ({
    name: eventType,
    value: count,
  }));


  return (
    <React.Fragment>
      <div>
      <h1>Bar Chart</h1>
      <BarChart data={eventStaticDataForChart} />
    </div>
    <div>
      <h1>Scatter Plot</h1>
      <ScatterPlot data={scatterPlotData} />
    </div>
    <h1>Real Time</h1>
    <div>
      <h1>Bar Chart</h1>
      <BarChart data={eventOnlineDataForChart} />
    </div>
    <div>
      <h1>Scatter Plot</h1>
      <ScatterPlot data={scatterOnlinePlotData} />
    </div>
    </React.Fragment>
  );
}

export default App;
