import React, { useState, useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import './App.css';
import BarChart from './components/charts/BarChart';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await fetchStaticDataFromAPI();
        setData(fetchedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Função para contar o número de eventos em cada array de dados
  const countEvents = (dataArray) => {
    const eventCounts = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        if (eventType in eventCounts) {
          eventCounts[eventType]++;
        } else {
          eventCounts[eventType] = 1;
        }
      });
    });

    return eventCounts;
  };

  const calculateEventDurations = (dataArray) => {
    const eventDurations = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        const timestamp = new Date(event.TimeStamp);

        if (eventType in eventDurations) {
          eventDurations[eventType] += timestamp - eventDurations[eventType].start;
        } else {
          eventDurations[eventType] = timestamp;
        }
      });
    });

    return eventDurations;
  };

  // Contagem de eventos
  const eventDataCounts = countEvents(data);
  const eventDurations = calculateEventDurations(data);

  // Conversão de contagens de eventos em um array de objetos para o gráfico de barras
  const eventDataForChart = Object.entries(eventDataCounts).map(([eventType, count]) => ({
    name: eventType,
    value: count,
  }));

  console.log('dados do gráfico:', eventDataForChart);
  console.log('Duração dos eventos:', eventDurations);

  return (
    <div>
      <h1>Bar Chart</h1>
      <BarChart data={eventDataForChart} />
    </div>
  );
}

export default App;
