import React, { useEffect, useState } from 'react'
import '../styles.css'
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap'
import {fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const [dataset, setDataset] = useState([])

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching PieChart data:', error)
      }
    }

    fetchStaticData().catch((error) =>
      console.error('Error fetching PieChart data:', error)
    )
  }, [])

  console.log("Aqui:", dataset)
  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown title="Choose your Dataset" id="basic-nav-dropdown-categories">
              {dataset.map((file, index) => (
                <NavDropdown.Item key={index} href={`/types?file=${file.file_name}`}>
                  {file.file_name} - Users: {file.user_count !== null ? file.user_count : 'N/A'}
                </NavDropdown.Item>
              ))}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
    </div>
  )
}

export default MainPage
