            </ListGroupItem>
            <ListGroupItem
              action
              tag="button"
            >
              Morbi leo risus
            </ListGroupItem>
            <ListGroupItem
              action
              tag="button"
            >
              Porta ac consectetur ac
            </ListGroupItem>
            <ListGroupItem
              action
              disabled
              tag="button"
            >
              Vestibulum at eros
            </ListGroupItem>
          </ListGroup>
          <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
            <DropdownToggle caret>Choose your Dataset</DropdownToggle>
            <DropdownMenu>
              {sortedDataset.map((file, index) => (
                <DropdownItem
                  key={index}
                  onClick={() => handleDatasetClick(file.dataset)}
                  id={`tooltip-${index}`}
                  onMouseEnter={() => toggleTooltip(index)}
                  onMouseLeave={() => toggleTooltip(index)}
                >
                  <div>
                    <strong>{formatDatasetName(file.dataset)}</strong>
                    <Tooltip
                      style={{
                        maxWidth: '300px',
                        padding: '10px',
                        borderRadius: '5px',
                        backgroundColor: '#f8f9fa',
                        boxShadow: '0 2px 5px rgba(0,0,0,0.15)',
                      }}
                      placement="right"
                      isOpen={tooltipOpen[index] || false}
                      target={`tooltip-${index}`}
                      toggle={() => toggleTooltip(index)} // Add toggle to ensure tooltip hides on click
                    >
                      <div style={{ textAlign: 'left' }}>
                        {tooltipData(file).map((item, i) => (
                          <div key={i} style={{ marginBottom: '10px' }}>
                            <div
                              style={{
                                fontWeight: 'bold',
                                color: '#333',
                                fontSize: '14px',
                              }}
                            >
                              {item.label}:
                            </div>
                            {typeof item.value === 'object' ? (
                              <ul
                                style={{
                                  padding: '0',
                                  margin: '0',
                                  listStyleType: 'none',
                                  fontSize: '13px',
                                  color: '#666',
                                }}
                              >
                                {Object.entries(item.value).map(
                                  ([key, val]) => (
                                    <li
                                      key={key}
                                      style={{ marginBottom: '5px' }}
                                    >
                                      {key}: {val}
                                    </li>
                                  )
                                )}
                              </ul>
                            ) : (
                              <div style={{ fontSize: '14px', color: '#333' }}>
                                {item.value}
                              </div>
                            )}
                          </div>
                        ))}
