import useEffect, { useState } from 'react'
import KafkaDataReceiver from '../../api/OnlineData'
import CountRealTimeEvents from './CountRealTimeEvents'

const RealTimeData = () => {
  const [realTimeData, setrealTimeData ] = useState([])

  useEffect(() => {
    const onDataReceived = (receivedData) => {
      const realEventCounts = CountRealTimeEvents(receivedData)
      setrealTimeData(realEventCounts)
      console.log('Event counts:', realEventCounts)
    }

    KafkaDataReceiver(onDataReceived)
  }, [])

  return realTimeData
}

export default RealTimeData;
