import React from 'react'
import SunBurst from '../../charts/SunBurst'

// Função para transformar os dados fornecidos em uma hierarquia
const transformDataToHierarchy = (data) => {
  const hierarchy = {
    name: 'User',
    children: [],
  }

  Object.entries(data).forEach(([user, events]) => {
    const sessionNode = {
      name: `User ${user}`,
      children: [],
    }

    events.forEach((event) => {
      const eventNode = {
        name: event.EventType,
        children: [],
      }

      Object.entries(event).forEach(([key, value]) => {
        if (typeof value === 'object' && value !== null) {
          const semanticNode = {
            name: key,
            children: Object.entries(value).map(([k, v]) => ({
              name: k,
              value: v,
            })),
          }
          eventNode.children.push(semanticNode)
        } else if (key !== 'EventType') {
          // Para os outros campos, exceto EventType que já está sendo usado como nome do evento
          eventNode.children.push({
            name: key,
            value: value,
          })
        }
      })

      sessionNode.children.push(eventNode)
    })

    hierarchy.children.push(sessionNode)
  })

  return hierarchy
}

const StaticSunBurst = ({ data }) => {
  const transformedData = React.useMemo(
    () => transformDataToHierarchy(data),
    [data]
  )
  console.log('SunburstData:', transformedData)
  return <SunBurst data={transformedData} />
}

export default StaticSunBurst
