import React, { useEffect, useState } from 'react'
import { fetchStaticCountWordsFromAPI } from '../../api/StaticData'
import WordCloud from '../charts/WordCloud'
import DonutChart from '../charts/PieChart'
import PieChart from '../charts/PieChart'

const WordCloudPage = () => {
  const [wordCloudData, setWordCloudData] = useState([])

  useEffect(() => {
    const fetchWordCloudData = async () => {
      try {
        const fetchedData = await fetchStaticCountWordsFromAPI()
        setWordCloudData(fetchedData)
      } catch (error) {
        console.error('Error fetching word cloud data:', error)
      }
    }

    fetchWordCloudData()
  }, [])
  const data = {
    width: 500,
    height: 500,
    colors: ['#ff0000', '#00ff00', '#0000ff'], // Array de cores
    dataSet: [
      { label: 'Categoria A', quantity: 20 },
      { label: 'Categoria B', quantity: 30 },
      { label: 'Categoria C', quantity: 50 },
    ],
  }

  return <PieChart data={data} />
}

export default WordCloudPage
