import React, { useEffect, useState } from 'react'
import '../styles.css'
import { useLocation } from 'react-router-dom'
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
  Dropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from 'reactstrap'
import ChooseCharts from '../chooseCharts/ChooseCharts'

const OverviewPage = () => {
  const location = useLocation()
  const [dropdownOpen, setDropdownOpen] = useState(false)
  const [windowWidth, setWindowWidth] = useState(window.innerWidth)
  const [visibleCharts, setVisibleCharts] = useState([])

  const receivedData = location.state?.receivedData || []

  const toggle = () => setDropdownOpen((prevState) => !prevState)

  const chartsData = [
    {
      title: 'SunBurst',
      activeLabel: 'SunBurst',
      data: receivedData,
      showChart: true,
    },
    {
      title: 'BoxPlot',
      activeLabel: 'BoxPlot',
      data: receivedData,
      showChart: true,
    },
    {
      title: 'WordCloud',
      activeLabel: 'WordCloud',
      data: receivedData,
      showChart: true,
    },
  ]

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth)
    window.addEventListener('resize', handleResize)

    return () => window.removeEventListener('resize', handleResize)
  }, [])

  useEffect(() => {
    if (windowWidth < 900) {
      setVisibleCharts(chartsData.slice(0, 2))
    } else {
      setVisibleCharts(chartsData)
    }
  }, [windowWidth])

  return (
    <div className="content full-height">
      <Row className="half-height">
        <Col style={{ maxHeight: '100%' }} xs="8">
          <ChooseCharts
            title={'SunBurst'}
            activeLabel={'SunBurst'}
            data={receivedData}
            showChart={true}
          />
        </Col>
        <Col className="card-unique-style" xs="4">
          <ChooseCharts
            title={'SunBurst'}
            activeLabel={'SunBurst'}
            data={receivedData}
            showChart={true}
          />
        </Col>
      </Row>
      <Row className="half-height">
        {visibleCharts.map((chart, index) => {
          let cardClass = ''
          switch (index) {
            case 0:
              cardClass = 'card-style-1'
              break
            case 1:
              cardClass = 'card-style-2'
              break
            case 2:
              cardClass = 'card-style-3'
              break
            default:
              break
          }

          return (
            <Col key={index} className={`half-height ${cardClass}`}>
              <ChooseCharts
                title={chart.title}
                activeLabel={chart.activeLabel}
                data={chart.data}
                showChart={chart.showChart}
              />
            </Col>
          )
        })}
      </Row>
    </div>
  )
}

export default OverviewPage
