import React, { useEffect, useState } from 'react'
import '../styles.css'
import { Container, Nav, NavDropdown } from 'react-bootstrap'
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const [dataset, setDataset] = useState([])
  const [showUsersList, setShowUsersList] = useState(false)
  const [selectedDataset, setSelectedDataset] = useState(null)
  const [selectedUsers, setSelectedUsers] = useState([])
  const [checkedUsers, setCheckedUsers] = useState([])
  const [usersDuration, setUsersDuration] = useState({})

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        // Map fetchedDataset to include both file_name and user_count
        const formattedDataset = fetchedDataset.map((data) => ({
          name: data.file_name,
          userCount: data.user_count !== null ? data.user_count : 0,
        }))
        setDataset(formattedDataset || [])
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const handleDatasetClick = async (datasetName, userCount) => {
    try {
      const fetchedUsersData = await fetchStaticAllFilesFromAPI(datasetName)
      setSelectedDataset(datasetName)
      setSelectedUsers(fetchedUsersData || [])
      setShowUsersList(true)
      setCheckedUsers([])
      setUsersDuration({})
    } catch (error) {
      console.error('Error fetching Users Data:', error)
    }
  }

  const renderDropdownItems = () => {
    return dataset.map((file, index) => (
      <NavDropdown.Item
        key={index}
        onClick={() => handleDatasetClick(file.name)}
      >
        <div>
          <strong>{file.name}</strong>
          <div style={{ fontSize: 'small', color: 'gray' }}>
            Users: {file.userCount}
          </div>
        </div>
      </NavDropdown.Item>
    ))
  }

  const handleBackClick = () => {
    setShowUsersList(false)
    setSelectedDataset(null)
    setSelectedUsers([])
    setCheckedUsers([])
    setUsersDuration({})
  }

  const handleCheckboxChange = (user) => {
    setCheckedUsers((prevCheckedUsers) =>
      prevCheckedUsers.includes(user)
        ? prevCheckedUsers.filter((u) => u !== user)
        : [...prevCheckedUsers, user]
    )
  }

  const calculateDurationForUser = (user) => {
    return usersDuration[user] || 0
  }

  const renderUsersList = () => {
    return (
      <div className="usersList">
        <ul>
          {selectedUsers.map((user, index) => (
            <li key={index} className="userItem">
              <input
                type="checkbox"
                checked={checkedUsers.includes(user)}
                onChange={() => handleCheckboxChange(user)}
                className="checkboxList"
              />
              {user}
              <div style={{ fontSize: 'small', color: 'gray' }}>
                Duration: {calculateDurationForUser(user)} seconds
              </div>
            </li>
          ))}
        </ul>
        <button className="submitButton" onClick={handleBackClick}>
          Submit
        </button>
      </div>
    )
  }

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown
              title="Choose your Dataset"
              id="basic-nav-dropdown-categories"
            >
              {renderDropdownItems()}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
      {showUsersList && renderUsersList()}
    </div>
  )
}

export default MainPage
