import React, { useEffect, useState } from 'react'
import '../styles.css'
import { Container, Nav, NavDropdown } from 'react-bootstrap'
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const [dataset, setDataset] = useState([])
  const [showUsersList, setShowUsersList] = useState(false)
  const [selectedUsers, setSelectedUsers] = useState([])

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const handleDatasetClick = (userCount) => {
    const users = Array.from(
      { length: userCount },
      (_, index) => `User ${index + 1}`
    )
    setSelectedUsers(users)
    setShowUsersList(true)
  }

  const handleBackClick = () => {
    setShowUsersList(false)
    setSelectedUsers([])
  }

  const sortedDataset = dataset.sort((a, b) =>
    a.file_name.localeCompare(b.file_name)
  )

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown
              title="Choose your Dataset"
              id="basic-nav-dropdown-categories"
            >
              {sortedDataset.map((file, index) => (
                <NavDropdown.Item
                  key={index}
                  onClick={() => handleDatasetClick(file.user_count)}
                >
                  <div>
                    <strong>{file.file_name}</strong>
                    <div style={{ fontSize: 'small', color: 'gray' }}>
                      Users: {file.user_count !== null ? file.user_count : 0}
                    </div>
                  </div>
                </NavDropdown.Item>
              ))}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
      {showUsersList && (
        <div className="users-list">
          <ul>
            {selectedUsers.map((user, index) => (
              <li key={index}>{user}</li>
            ))}
          </ul>
          <button onClick={handleBackClick}>Back</button>
        </div>
      )}
    </div>
  )
}

export default MainPage
