const transformDataToSankeyChart = (data) => {
  console.log('Received data:', data)

  if (!data || typeof data !== 'object') {
    throw new Error('Invalid data: Data must be an object.')
  }

  const nodesSet = new Set()
  const links = []

  // Itera sobre as chaves do objeto `data`
  Object.keys(data).forEach((key) => {
    data[key].forEach((entry) => {
      const { Source, Target, Start, End } = entry

      // Adiciona os nós
      nodesSet.add(Source)
      nodesSet.add(Target)

      // Calcula a duração (em segundos ou em alguma unidade)
      const duration = (new Date(End) - new Date(Start)) / 1000 // Duração em segundos

      // Adiciona o link
      links.push({
        source: Source,
        target: Target,
        value: duration,
      })
    })
  })

  // Converte o Set de nós em um array
  const nodes = Array.from(nodesSet).map((name) => ({
    name,
    category: 'default', // Você pode modificar isso com base na lógica necessária
  }))

  return { nodes, links }
}

export default transformDataToSankeyChart
