import React, { useState, useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import './App.css';
import BarChart from './components/charts/BarChart';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await fetchStaticDataFromAPI();
        setData(fetchedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Função para contar o número de eventos em cada array de dados
  const countEvents = (dataArray) => {
    const eventCounts = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        if (eventType in eventCounts) {
          eventCounts[eventType]++;
        } else {
          eventCounts[eventType] = 1;
        }
      });
    });

    return eventCounts;
  };



  // Processamento dos dados
  const eventCounts = countEvents(data);
  const eventDurations = calculateEventDurations(data);

  // Conversão de contagens de eventos em um array de objetos para o gráfico de barras
  const eventDataForChart = Object.entries(eventCounts).map(([eventType, count]) => ({
  name: eventType,
  count: count,
  }));

  console.log('Contagem de eventos:', eventDataForChart);
  console.log('Duração dos eventos:', eventDurations);

  return (
    <div>
      <h1>Bar Charts</h1>
      <div>
        <h2>Contagem de Eventos</h2>
        <BarChart data={eventDataForChart} />
      </div>

    </div>
  );
}

export default App;
