import React, { useState, useEffect } from 'react'
import transformDataToSankeyChart from './path/to/transformDataToSankeyChart' // Importa a função que você criou

const SankeyChartComponent = ({ data, expParam }) => {
  const [sankeyData, setSankeyData] = useState(null)
  const [error, setError] = useState(null)

  useEffect(() => {
    try {
      const result = transformDataToSankeyChart(data, expParam)
      if (result.error) {
        setError(result.error)
      } else {
        setSankeyData(result)
        setError(null) // Limpa o erro se os dados forem válidos
      }
    } catch (err) {
      setError('An unexpected error occurred')
    }
  }, [data, expParam])

  if (error) {
    return <div>Error: {error}</div> // Renderiza a mensagem de erro
  }

  if (!sankeyData) {
    return <div>Loading...</div> // Enquanto os dados não estão prontos
  }

  // Aqui você renderiza o gráfico de Sankey usando os dados formatados
  return (
    <div>
      {/* Renderiza o gráfico de Sankey usando sankeyData.nodes e sankeyData.links */}
      <h1>Sankey Chart</h1>
      {/* Exemplo de renderização dos nós */}
      <ul>
        {sankeyData.nodes.map((node, index) => (
          <li key={index}>
            {node.name} - {node.category}
          </li>
        ))}
      </ul>
      {/* Aqui você adicionaria seu componente de gráfico de Sankey */}
    </div>
  )
}

export default SankeyChartComponent
