import React, { useEffect, useState } from 'react'
import { fetchSunBurstFromAPI } from '../../../api/StaticData'
import SunBurst from '../../charts/SunBurst'

const StaticSunBurstChart = (onDataReceived, onWordDataReceived) => {
  const [sunData, setSunData] = useState([])
  const [eventData, setEventData] = useState({})
  const [eventWordData, setEventWordData] = useState({})
  const [dataLoaded, setDataLoaded] = useState(false)

  const handleDataReceived = (data) => {
    setEventData(data)
  }

  const handleWordDataReceived = (data) => {
    setEventWordData(data)
  }

  useEffect(() => {
    const fetchSunBurst = async () => {
      try {
        const fetchedStaticData = await fetchSunBurstFromAPI()
        setSunData(fetchedStaticData)
        setDataLoaded(true)
      } catch (error) {
        console.error('Failed to fetch sunburst data:', error)
      }
    }

    fetchSunBurst().catch(console.error)
  }, [])

  return dataLoaded && <SunBurst data={sunData} />
}

export default StaticSunBurstChart
