import * as d3 from 'd3';

const TimelineChart = (data) => {
  // Dimensões do gráfico
  const width = 800;
  const height = 400;

  // Margens
  const margin = { top: 20, right: 30, bottom: 30, left: 60 };
  const innerWidth = width - margin.left - margin.right;
  const innerHeight = height - margin.top - margin.bottom;

  // Seleciona o elemento SVG onde o gráfico será desenhado
  const svg = d3.select('svg')
    .attr('width', width)
    .attr('height', height);

  // Escala de tempo
  const xScale = d3.scaleTime()
    .domain([
      d3.min(data, d => d3.min(d, e => new Date(e.Start))),
      d3.max(data, d => d3.max(d, e => new Date(e.End)))
    ])
    .range([margin.left, innerWidth + margin.left]);

  // Escala linear para o eixo Y
  const yScale = d3.scaleLinear()
    .domain([0, data.length])
    .range([margin.top, innerHeight + margin.top]);

  // Desenha as linhas do gráfico
  svg.selectAll('line')
    .data(data)
    .enter().append('line')
    .attr('x1', d => xScale(new Date(d[0].Start)))
    .attr('y1', (d, i) => yScale(i))
    .attr('x2', d => xScale(new Date(d[d.length - 1].End)))
    .attr('y2', (d, i) => yScale(i))
    .attr('stroke', 'black');

  // Desenha os círculos para marcar os eventos
  svg.selectAll('circle')
    .data(data)
    .enter().append('circle')
    .attr('cx', d => xScale(new Date(d[0].Start)))
    .attr('cy', (d, i) => yScale(i))
    .attr('r', 5)
    .attr('fill', 'red');

  // Eixo X
  const xAxis = d3.axisBottom(xScale);
  svg.append('g')
    .attr('transform', `translate(0, ${innerHeight + margin.top})`)
    .call(xAxis);
};

export default TimelineChart;
