import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import {
  Container,
  ListGroup,
  ListGroupItem,
  Card,
  CardBody,
  CardTitle,
  CardText,
  Row,
  Col,
} from 'reactstrap'
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const navigate = useNavigate()
  const [dataset, setDataset] = useState([])
  const [selectedDataset, setSelectedDataset] = useState(null)

  useEffect(() => {
    const fetchDataset = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching Dataset:', error)
      }
    }

    fetchDataset().catch((error) =>
      console.error('Error fetching Dataset:', error)
    )
  }, [])

  const formatDatasetName = (filename) => {
    const nameWithoutExtension = filename.replace('.json', '')
    return nameWithoutExtension
      .replace(/^dataset/i, 'Dataset')
      .replace(/(\d+)/, ' $1')
  }

  const handleDatasetClick = (dataset) => {
    setSelectedDataset(dataset)
  }

  const datasetFile = (file) => [
    {
      label: 'Name',
      value: file.dataset ? file.dataset : 'No dataset Name',
    },
    {
      label: 'Number of Users',
      value: file.users ? file.users : '0',
    },
    {
      label: 'Dataset Time',
      value: file.total_duration ? `${file.total_duration} s` : '0 s',
    },
    {
      label: 'Number of Types',
      value: file.type_counts
        ? JSON.stringify(file.type_counts, null, 2)
        : 'No data',
    },
    {
      label: 'Number of Event Types',
      value: file.event_type_counts
        ? JSON.stringify(file.event_type_counts, null, 2)
        : 'No data',
    },
  ]

  const sortedDataset = dataset.sort((first, second) =>
    first.dataset.localeCompare(second.dataset)
  )

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>

      {/* Grid com duas secções */}
      <div className="grid-container">
        {/* Primeira secção com a lista de datasets */}
        <div className="grid-item">
          <Container>
            <ListGroup>
              {sortedDataset.map((file, index) => (
                <ListGroupItem
                  key={index}
                  action
                  tag="button"
                  onClick={() => handleDatasetClick(file)}
                  className="dataset-list-item"
                >
                  {formatDatasetName(file.dataset)}
                </ListGroupItem>
              ))}
            </ListGroup>
          </Container>
        </div>

        {/* Segunda secção para mostrar os detalhes do dataset */}
        <div className="grid-item">
          {selectedDataset ? (
            <Container>
              <h3 className="mb-4">Dataset Information</h3>
              <Row>
                {datasetFile(selectedDataset).map((info, index) => (
                  <Col md="6" key={index}>
                    <Card className="mb-4">
                      <CardBody>
                        <CardTitle tag="h5" className="text-primary">
                          {info.label}
                        </CardTitle>
                        <CardText>
                          <pre
                            className="text-muted"
                            style={{ whiteSpace: 'pre-wrap' }}
                          >
                            {info.value}
                          </pre>
                        </CardText>
                      </CardBody>
                    </Card>
                  </Col>
                ))}
              </Row>
            </Container>
          ) : (
            <p>Select a dataset to view its details</p>
          )}
        </div>
      </div>
    </div>
  )
}

export default MainPage
