import React, { useEffect, useRef } from 'react'
import * as d3 from 'd3'

const StackedBarChart = ({ data }) => {
  const ref = useRef()

  useEffect(() => {
    console.log('MyData:', data)
    const generateChart = (data) => {
      if (!data || data.length === 0) {
        console.error('No data available to generate the chart.')
        return
      }

      const width = 928
      const height = 500
      const marginTop = 10
      const marginRight = 10
      const marginBottom = 20
      const marginLeft = 40

      // Agrupar os dados pelo "name"
      const groupedData = d3.group(data, (d) => d.name)
      if (!groupedData || groupedData.size === 0) {
        console.error('Grouped data is undefined or empty.')
        return
      }

      // Extrair os tipos únicos (INPUT, OUTPUT) do data
      const allTypes = Array.from(new Set(data.map((d) => d.type)))
      if (!allTypes || allTypes.length === 0) {
        console.error("No valid 'type' found in data.")
        return
      }

      // Criar as séries empilhadas (a chave é o type)
      const series = d3
        .stack()
        .keys(allTypes) // Empilhar pelas chaves (types)
        .value((group, key) => {
          const item = group.find((d) => d.type === key)
          return item ? item.count : 0 // Usa o valor de `count` para empilhar
        })(Array.from(groupedData.values()))

      if (!series || series.length === 0) {
        console.error('Series data is undefined or empty.')
        return
      }

      // Verificação do domínio do eixo X
      const xDomain = [...groupedData.keys()]
      if (!xDomain || xDomain.length === 0) {
        console.error('xDomain is undefined or empty.')
        return
      }

      // Eixo X
      const x = d3
        .scaleBand()
        .domain(xDomain)
        .range([marginLeft, width - marginRight])
        .padding(0.1)

      // Verificação do domínio Y baseado nos dados empilhados
      const yMax = d3.max(series, (d) => d3.max(d, (d) => d[1])) // Valor máximo dos stacks
      if (yMax === undefined || yMax === null) {
        console.error('yMax is undefined or invalid.')
        return
      }

      const y = d3
        .scaleLinear()
        .domain([0, yMax])
        .rangeRound([height - marginBottom, marginTop])

      // Definir as cores de preenchimento
      const color = d3
        .scaleOrdinal()
        .domain(allTypes)
        .range(d3.schemeSpectral[allTypes.length] || ['#ccc'])

      // Criar o SVG
      const svg = d3
        .select(ref.current)
        .append('svg')
        .attr('width', width)
        .attr('height', height)
        .attr('viewBox', [0, 0, width, height])
        .attr('style', 'max-width: 100%; height: auto;')

      // Adicionar barras empilhadas
      svg
        .append('g')
        .selectAll('g')
        .data(series) // Dados empilhados
        .join('g')
        .attr('fill', (d) => color(d.key)) // Mapeia o tipo ao esquema de cores
        .selectAll('rect')
        .data((d) => d) // Cada série empilhada contém várias "fatias" por barra
        .join('rect')
        .attr('x', (d) => x(d.data[0].name)) // Nome do grupo (barra)
        .attr('y', (d) => y(d[1])) // Posição Y do topo do retângulo
        .attr('height', (d) => y(d[0]) - y(d[1])) // Altura do retângulo
        .attr('width', x.bandwidth()) // Largura da barra
        .append('title')
        .text((d) => {
          const user = d.data[0].name // Nome do usuário
          const count = d[1] - d[0] // Contagem do empilhamento
          const type = d3.keys(d.data[0])[0] // O tipo (INPUT, OUTPUT)
          return `User: ${user || 'N/A'}\nType: ${type || 'N/A'}\nCount: ${count}`
        })

      // Adicionar eixos
      svg
        .append('g')
        .attr('transform', `translate(0,${height - marginBottom})`)
        .call(d3.axisBottom(x).tickSizeOuter(0))
        .call((g) => g.selectAll('.domain').remove())

      svg
        .append('g')
        .attr('transform', `translate(${marginLeft},0)`)
        .call(d3.axisLeft(y).ticks(null, 's'))
        .call((g) => g.selectAll('.domain').remove())

      return svg.node()
    }

    if (data && data.length > 0) {
      d3.select(ref.current).selectAll('*').remove() // Limpar gráfico anterior
      const svg = generateChart(data)
      ref.current.appendChild(svg)
    }
  }, [data])

  return <div ref={ref}></div>
}

export default StackedBarChart
