import React, { useEffect, useState } from 'react'
import '../styles.css'
import { Container, Nav, NavDropdown } from 'react-bootstrap'
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData'

const MainPage = () => {
  const [dataset, setDataset] = useState([])
  const [showDiv, setShowDiv] = useState(false)
  const [users, setUsers] = useState([])

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI()
        setDataset(fetchedDataset)
      } catch (error) {
        console.error('Error fetching PieChart data:', error)
      }
    }

    fetchStaticData().catch((error) =>
      console.error('Error fetching PieChart data:', error)
    )
  }, [])

  const handleItemClick = async (file_name) => {
    setShowDiv(true)
    try {
      const response = await fetch(`/users?file=${file_name}`)
      const data = await response.json()
      setUsers(data)
    } catch (error) {
      console.error('Error fetching user data:', error)
    }
  }

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown title="Choose your Dataset" id="basic-nav-dropdown-categories">
              {dataset.map((file, index) => (
                <NavDropdown.Item
                  key={index}
                  href={`/types?file=${file.file_name}`}
                  onClick={() => handleItemClick(file.file_name)}
                >
                  <div>
                    <strong>{file.file_name}</strong>
                    <div style={{ fontSize: 'small', color: 'gray' }}>
                      Users: {file.user_count !== null ? file.user_count : 0}
                    </div>
                  </div>
                </NavDropdown.Item>
              ))}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
      {showDiv && (
        <div className="hello-div">
          Ol√°
          <div className="users-list">
            {users.length > 0 ? (
              <ul>
                {users.map((user, index) => (
                  <li key={index}>{user.name}</li>
                ))}
              </ul>
            ) : (
              <p>No users found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

export default MainPage
