import React, { useEffect, useState } from 'react'
import {
  fetchStaticPies2FromAPI,
  fetchStaticPiesFromAPI,
} from '../../../api/StaticData'
import PieChart from '../../charts/PieChart'

const StaticPie = ({ secondPieChart }) => {
  const [pieData, setPieData] = useState([])
  const [dataLoaded, setDataLoaded] = useState(false)
  const [test, setTest] = useState()

  useEffect(() => {
    const fetchWordCloud = async () => {
      try {
        let fetchedPieData = {}
        if (secondPieChart) {
          console.log('Entra')
          fetchedPieData = await fetchStaticPies2FromAPI()
          secondPieChart = false
        } else {
          fetchedPieData = await fetchStaticPiesFromAPI()
        }
        setPieData(fetchedPieData)
        setDataLoaded(true)
        secondPieChart = false
      } catch (error) {
        console.error('Error fetching PieChart data:', error)
      }
    }

    fetchWordCloud().catch((error) =>
      console.error('Error fetching PieChart data:', error)
    )
  }, [secondPieChart])

  return dataLoaded && <PieChart data={pieData} />
}

export default StaticPie
