import React, { useEffect, useRef } from 'react'
import * as d3 from 'd3'

const BoxPlot = ({ data }) => {
  const svgRef = useRef()

  useEffect(() => {
    d3.select(svgRef.current).selectAll('*').remove()

    if (!data || data.length === 0) {
      return
    }

    const width = 928
    const height = 600
    const marginTop = 20
    const marginRight = 20
    const marginBottom = 30
    const marginLeft = 40


    // Bin the data and derive the values (inter-quartile range, outliers…) for each bin.
    const n = width / 40;
    const bins = d3.bin()
      .thresholds(n)
      .value(d => d.carat)
      (diamonds)
      .map(bin => {
        bin.sort((a, b) => a.price - b.price);
        const values = bin.map(d => d.price);
        const min = values[0];
        const max = values[values.length - 1];
        const q1 = d3.quantile(values, 0.25);
        const q2 = d3.quantile(values, 0.50);
        const q3 = d3.quantile(values, 0.75);
        const iqr = q3 - q1; // interquartile range
        const r0 = Math.max(min, q1 - iqr * 1.5);
        const r1 = Math.min(max, q3 + iqr * 1.5);
        bin.quartiles = [q1, q2, q3];
        bin.range = [r0, r1];
        bin.outliers = bin.filter(v => v.price < r0 || v.price > r1); // TODO
        return bin;
      })


    const boxWidth = 70
    const minBoxSpacing = 30
    const totalWidth = (boxWidth + minBoxSpacing) * data.length

    const minValues = data.map((dataset) => d3.min(dataset.values))
    const maxValues = data.map((dataset) => d3.max(dataset.values))
    const minAll = d3.min(minValues) - 2
    const maxAll = d3.max(maxValues) + 2

    const y = d3.scaleLinear().domain([minAll, maxAll]).range([height, 0])

    const svg = d3
      .select(svgRef.current)
      .attr('width', '100%')
      .attr('height', '100%')
      .attr(
        'viewBox',
        `0 0 ${totalWidth + marginLeft + marginRight} ${height + marginTop + marginBottom}`
      )
      .style('font', '10px sans-serif')

    const g = svg
      .append('g')
      .attr('transform', `translate(${marginLeft},${marginTop})`)

    const datasetWidth = totalWidth / data.length

    data.forEach((dataset, index) => {
      const data_sorted = dataset.values.sort(d3.ascending)
      const q1 = d3.quantile(data_sorted, 0.25)
      const median = d3.quantile(data_sorted, 0.5)
      const q3 = d3.quantile(data_sorted, 0.75)
      const interQuantileRange = q3 - q1
      const min = Math.min(maxAll, q3 + interQuantileRange * 1.5)
      const max = Math.max(minAll, q1 - interQuantileRange * 1.5)
      const x = index * datasetWidth + datasetWidth / 2

      svg
        .append('line')
        .attr('x1', x)
        .attr('x2', x)
        .attr('y1', y(min))
        .attr('y2', y(max))
        .attr('stroke', 'black')

      svg
        .append('rect')
        .attr('x', x - boxWidth / 2)
        .attr('y', y(q3))
        .attr('height', y(q1) - y(q3))
        .attr('width', boxWidth)
        .attr('stroke', 'black')
        .style('fill', '#69b3a2')

      svg
        .selectAll('line.toto')
        .data([min, median, max])
        .enter()
        .append('line')
        .attr('x1', x - boxWidth / 2)
        .attr('x2', x + boxWidth / 2)
        .attr('y1', (d) => y(d))
        .attr('y2', (d) => y(d))
        .attr('stroke', 'black')

      svg
        .append('text')
        .attr('x', x)
        .attr('y', height + marginBottom / 2)
        .attr('text-anchor', 'middle')
        .text(dataset.label)
        .style('font-size', '12px')
        .attr('fill', 'black')
    })

    svg.append('g').attr('class', 'y-axis').call(d3.axisLeft(y))
  }, [data])

  return <svg ref={svgRef} style={{ width: '100%', height: '100%' }} />
}

export default BoxPlot
