import React, { useEffect, useRef } from 'react'
import * as d3 from 'd3'

const TimelineChart = ({
  data,
  handleLabelClick,
  onDataReceived,
  onWordDataReceived,
  onExpDataReceived,
  eventColor,
}) => {
  const svgRef = useRef()

  useEffect(() => {
    if (data && data.length > 0) {
      drawTimeline(data)
    }
  }, [data, eventColor])

  const drawTimeline = (data) => {
    const svg = d3.select(svgRef.current)
    svg.selectAll('*').remove()

    const margin = { top: 20, right: 15, bottom: 30, left: 100 }
    const width = +svg.attr('width') - margin.left - margin.right
    const height = +svg.attr('height') - margin.top - margin.bottom

    // Cálculo dos tempos de início e fim
    const allStartTimes = data.flatMap((d) =>
      d.times.map((t) => t.starting_time)
    )
    const allEndTimes = data.flatMap((d) => d.times.map((t) => t.ending_time))

    const x = d3
      .scaleTime()
      .domain([
        d3.min(allStartTimes) || new Date(), // Se não houver, usa a data atual
        d3.max(allEndTimes) || new Date(), // Se não houver, usa a data atual
      ])
      .range([0, width])

    const y = d3
      .scaleBand()
      .domain(data.map((d) => d.label))
      .range([0, height])
      .padding(0.1)

    const xAxis = svg
      .append('g')
      .attr('class', 'x-axis')
      .attr('transform', `translate(${margin.left},${height + margin.top})`)
      .call(d3.axisBottom(x).tickSizeOuter(0))

    const yAxis = svg
      .append('g')
      .attr('class', 'y-axis')
      .attr('transform', `translate(${margin.left},${margin.top})`)
      .call(d3.axisLeft(y).tickSize(0).tickPadding(6))

    const barsGroup = svg
      .append('g')
      .attr('class', 'bars-group')
      .attr('transform', `translate(${margin.left},${margin.top})`)

    // Define a escala de cores
    const colorScale = d3.scaleOrdinal(d3.schemeCategory10)

    const eventsGroup = barsGroup
      .selectAll('g')
      .data(data)
      .join('g')
      .attr('class', 'event-group')
      .selectAll('rect')
      .data((d) => d.times)
      .join('rect')
      .attr('class', 'event-rect')
      .attr('x', (d) => x(d.starting_time))
      .attr('y', (d) => y(data.find((p) => p.times.includes(d)).label))
      .attr('width', (d) => Math.max(0, x(d.ending_time) - x(d.starting_time))) // Garante que a largura não seja negativa
      .attr('height', y.bandwidth())
      .attr('fill', (d, i) => {
        if (eventColor && eventColor.length > 0) {
          let color = '#636363'
          data.forEach((event) => {
            event.times.forEach((time) => {
              if (time === d && event.type === eventColor) {
                color = colorScale(i)
              }
            })
          })
          return color
        } else {
          return colorScale(i)
        }
      })

    // Remover texto do eixo Y
    yAxis.selectAll('text').remove()

    // Adicionar botões
    yAxis
      .selectAll('foreignObject')
      .data(data)
      .enter()
      .append('foreignObject')
      .attr('x', -margin.left)
      .attr('y', (d) => y(d.label))
      .attr('width', margin.left - 10)
      .attr('height', y.bandwidth())
      .append('xhtml:div')
      .html(
        (d) =>
          `<button class="btn btn-dark d-flex align-items-center justify-content-center" style="width:100%; height:${y.bandwidth()}px; padding-top:10px; padding-bottom:10px;">${d.label}</button>`
      )

    // Comportamento de zoom
    const zoomBehavior = d3
      .zoom()
      .scaleExtent([1, 10])
      .translateExtent([
        [0, 0],
        [width, height],
      ])
      .extent([
        [0, 0],
        [width, height],
      ])
      .on('zoom', (event) => {
        const newX = event.transform.rescaleX(x)
        xAxis.call(d3.axisBottom(newX).tickSizeOuter(0))

        eventsGroup
          .attr('x', (d) => newX(d.starting_time))
          .attr('width', (d) =>
            Math.max(0, newX(d.ending_time) - newX(d.starting_time))
          ) // Garante que a largura não seja negativa durante o zoom
      })

    // Aplicar comportamento de zoom
    svg.call(zoomBehavior)
  }

  return <svg ref={svgRef} width={1300} height={400}></svg>
}

export default TimelineChart
