import * as d3 from 'd3';

const TimelineChart = (svg, data) => {
    // Flatten the data array
    const flattenedData = [].concat(...data);

    // Escala de tempo
    const x = d3.scaleTime()
        .domain([d3.min(flattenedData, function(d) { return d.Start; }), d3.max(flattenedData, function(d) { return d.End; })])
        .range([0, svg.attr("width")]);

    // Linhas do gráfico
    svg.selectAll("line")
        .data(data)
        .enter().append("line")
        .attr("x1", function(d) { return x(d[0].Start); })
        .attr("y1", function(d, i) { return i * 50 + 25; })
        .attr("x2", function(d) { return x(d[d.length - 1].End); })
        .attr("y2", function(d, i) { return i * 50 + 25; })
        .attr("stroke", "black");

    // Pontos de início e fim
    svg.selectAll("circle")
        .data(flattenedData)
        .enter().append("circle")
        .attr("cx", function(d) { return x(d.Start); })
        .attr("cy", function(d, i) { return i * 50 + 25; })
        .attr("r", 5)
        .attr("fill", "red");

    // Eixo X
    svg.append("g")
        .attr("transform", "translate(0," + (svg.attr("height") - 20) + ")")
        .call(d3.axisBottom(x));
}

export default TimelineChart;
