import TimelineChart from '../../charts/TimeLineChart'
import React, { useEffect, useState } from 'react'
import {
  fetchBoxPlotFromAPI,
  fetchStaticTimeLineFromAPI,
} from '../../../api/StaticData'

const StaticTimeLine = ({ onDataReceived, onWordDataReceived, data }) => {
  const [timesData, setTimesData] = useState([])
  const [dataLoaded, setDataLoaded] = useState(false)

  useEffect(() => {
    const fetchTimeLineData = async () => {
      try {
        let fetchedBoxPlotData = []
        if (data) {
          fetchedBoxPlotData = data
        } else {
          fetchedBoxPlotData = await fetchBoxPlotFromAPI()
        }
        const fetchedData = await fetchStaticTimeLineFromAPI()
        setTimesData(fetchedData)
        setDataLoaded(true)
      } catch (error) {
        console.error('Error fetching TimeLine data:', error)
      }
    }

    fetchTimeLineData().catch((error) =>
      console.error('Error fetching TimeLine data:', error)
    )
  }, [])

  const handleLabelClick = async (label) => {
    const experienceId = label.split(' ')[0]
    const fetchedData = await fetchStaticTimeLineFromAPI(experienceId)
    setTimesData(fetchedData)
    setDataLoaded(true)
  }

  return (
    dataLoaded && (
      <TimelineChart
        data={timesData}
        handleLabelClick={handleLabelClick}
        onDataReceived={onDataReceived}
        onWordDataReceived={onWordDataReceived}
      />
    )
  )
}

export default StaticTimeLine
