import React, { useEffect } from 'react'
import * as d3 from 'd3'

const MultiLevelPieChart = () => {
  useEffect(() => {
    drawChart()
  }, [])

  const drawChart = () => {
    d3.select('#chart-container').selectAll('*').remove()

    const width = 400
    const height = 400
    const radius = Math.min(width, height) / 2

    const color = d3.scaleOrdinal(d3.schemeCategory10)

    const svg = d3
      .select('#chart-container')
      .append('svg')
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2},${height / 2})`)

    const data = {
      children: [
        {
          children: [
            {
              name: 'AFFIRM',
              value: 6,
            },
          ],
          name: 'Speech Input',
          type: 'test',
        },
        {
          children: [
            {
              name: 'AFFIRM',
              value: 6,
            },
          ],
          name: 'Gesture Input',
        },
        {
          children: [
            {
              name: 'AFFIRM',
              value: 2,
            },
          ],
          name: 'Fusion',
        },
        {
          children: [
            {
              name: 'I AM GLAD YOU LIKED MY SUGGESTION, I WILL PLAY THE SONG THEN',
              value: 6,
            },
          ],
          name: 'Response Generator',
        },
        {
          children: [
            {
              name: 'I AM GLAD YOU LIKED MY SUGGESTION, I WILL PLAY THE SONG THEN',
              value: 4,
            },
            {
              name: ' Ola I AM GLAD YOU LIKED MY SUGGESTION, I WILL PLAY THE SONG THEN',
              value: 2,
            },
          ],
          name: 'Response Render',
        },
        {
          children: [
            {
              name: 'AFFIRM',
              value: 6,
            },
          ],
          name: 'ERROR Input',
        },
        {
          children: [
            {
              name: 'AFFIRM',
              value: 4,
            },
          ],
          name: 'Fusion',
        },
      ],
    }

    const partition = d3.partition().size([2 * Math.PI, radius])

    const root = d3.hierarchy(data).sum((d) => d.value)

    partition(root)

    const arc = d3
      .arc()
      .startAngle((d) => d.x0)
      .endAngle((d) => d.x1)
      .innerRadius((d) => d.y0)
      .outerRadius((d) => d.y1)

    svg
      .selectAll('path')
      .data(root.descendants().slice(1))
      .enter()
      .append('path')
      .attr('d', arc)
      .style('stroke', '#fff')
      .style('fill', (d) => color(d.data.name))
      .append('title')
      .text((d) => `${d.data.name}: ${d.value}`)
  }

  return <div id="chart-container"></div>
}

export default MultiLevelPieChart
