import React, { useState, useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import './App.css';
import BarChart from './components/charts/BarChart';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await fetchStaticDataFromAPI();
        setData(fetchedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Função para contar o número de eventos em cada array de dados
  const processEventData = (dataArray) => {
    const eventCounts = {};
    const eventDurations = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        if (eventType in eventCounts) {
          eventCounts[eventType]++;
        } else {
          eventCounts[eventType] = 1;
        }


      });
    });

    return {eventCounts, eventDurations};
  };

  // Contagem de eventos
  const {eventCounts, eventDurations} = processEventData(data);

  // Conversão de contagens de eventos em um array de objetos para o gráfico de barras
  const eventDataForChart = Object.entries(eventCounts).map(([eventType, count]) => ({
    name: eventType,
    count: count,
  }));

  console.log('dados do gráfico:', eventDataForChart);
  console.log('duração do gráfico:', eventDurations);


  return (
    <div>
      <h1>Bar Chart</h1>
      <BarChart data={eventDataForChart} />
    </div>
  );
}

export default App;
