import React, { useEffect, useState } from 'react';
import RealTimeBarChart from './charts/RealTimeBarChart';
import KafkaDataReceiver from '../../api/OnlineData';
import CountRealTimeEvents from './CountRealTimeEvents';

const RealTimeData = () => {
  const [realTimeData, setRealTimeData] = useState([]);

  useEffect(() => {
    const onDataReceived = (receivedData) => {
      const realEventCounts = CountRealTimeEvents(receivedData);
      const newData = Object.entries(realEventCounts).map(([eventType, count]) => ({
        name: eventType,
        value: count,
      }));
      setRealTimeData(newData);
      console.log('Event counts:', newData);
    };

    KafkaDataReceiver(onDataReceived);
    return newData
  }, []);

  return (
    <React.Fragment>
      <div>
        <h1>Real-Time Bar Chart</h1>
        <RealTimeBarChart data={newData} />
      </div>
    </React.Fragment>
  );
};

export default RealTimeData;
