import * as d3 from 'd3';

const TimelineChart = (data) => {
    // Configuração do SVG
    const svg = d3.select("svg"),
          width = +svg.attr("width"),
          height = +svg.attr("height");

    // Converter strings de data e hora para objetos Date
    const parseTime = d3.timeParse("%Y-%m-%d %H:%M:%S.%L");
    data.forEach(function(eventList) {
        eventList.forEach(function(event) {
            event.Start = parseTime(event.Start);
            event.End = parseTime(event.End);
        });
    });

    // Escala de tempo
    const x = d3.scaleTime()
        .domain([d3.min(data.flat(), function(d) { return d.Start; }), d3.max(data.flat(), function(d) { return d.End; })])
        .range([0, width]);

    // Linhas do gráfico
    svg.selectAll("line")
        .data(data)
        .enter().append("line")
        .attr("x1", function(d) { return x(d[0].Start); })
        .attr("y1", function(d, i) { return i * 50 + 25; })
        .attr("x2", function(d) { return x(d[d.length - 1].End); })
        .attr("y2", function(d, i) { return i * 50 + 25; })
        .attr("stroke", "black");

    // Pontos de início e fim
    svg.selectAll("circle")
        .data(data.flat())
        .enter().append("circle")
        .attr("cx", function(d) { return x(d.Start); })
        .attr("cy", function(d, i) { return i * 50 + 25; })
        .attr("r", 5)
        .attr("fill", "red");

    // Eixo X
    svg.append("g")
        .attr("transform", "translate(0," + (height - 20) + ")")
        .call(d3.axisBottom(x));
}

export default TimelineChart;
