const transformDataStackedBar = (data) => {
  const result = []

  if (!data || typeof data !== 'object') {
    console.error('Data is undefined or in an invalid format')
    return result // Retorna um array vazio para evitar que o restante do código falhe
  }

  Object.keys(data).forEach((user) => {
    const events = data[user]

    if (!Array.isArray(events)) {
      console.warn(
        `Expected an array of events for user ${user}, but got`,
        events
      )
      return
    }

    const typeCounts = {}

    events.forEach((event) => {
      const eventType = event.Type

      if (typeCounts[eventType]) {
        typeCounts[eventType]++
      } else {
        typeCounts[eventType] = 1
      }
    })

    Object.keys(typeCounts).forEach((type) => {
      result.push({
        name: user,
        type: type,
        count: typeCounts[type],
      })
    })
  })

  return result
}

export default transformDataStackedBar
