import React, { useEffect, useState } from 'react';
import '../styles.css';
import { Container, Nav, NavDropdown } from 'react-bootstrap';
import { fetchStaticAllFilesFromAPI } from '../../api/StaticData';

const MainPage = () => {
  const [dataset, setDataset] = useState([]);
  const [showUsersList, setShowUsersList] = useState(false);
  const [selectedUsers, setSelectedUsers] = useState([]);

  useEffect(() => {
    const fetchStaticData = async () => {
      try {
        const fetchedDataset = await fetchStaticAllFilesFromAPI();
        setDataset(fetchedDataset);
      } catch (error) {
        console.error('Error fetching PieChart data:', error);
      }
    };

    fetchStaticData().catch((error) =>
      console.error('Error fetching PieChart data:', error)
    );
  }, []);

  const handleDatasetClick = (file) => {
    setSelectedUsers(file.users || []);
    setShowUsersList(true);
  };

  return (
    <div className="main-container">
      <div className="title">
        <h1 className="title-text">
          Visualization and Analysis <br /> of <br /> Multimodal Interaction
        </h1>
      </div>
      <div className="nav-container">
        <Container>
          <Nav className="me-auto">
            <NavDropdown title="Choose your Dataset" id="basic-nav-dropdown-categories">
              {dataset.map((file, index) => (
                <NavDropdown.Item
                  key={index}
                  onClick={() => handleDatasetClick(file)}
                >
                  <div>
                    <strong>{file.file_name}</strong>
                    <div style={{ fontSize: 'small', color: 'gray' }}>
                      Users: {file.user_count !== null ? file.user_count : 0}
                    </div>
                  </div>
                </NavDropdown.Item>
              ))}
            </NavDropdown>
          </Nav>
        </Container>
      </div>
      {showUsersList && (
        <div className="users-list">
          {selectedUsers.length > 0 ? (
            <ul>
              {selectedUsers.map((user, index) => (
                <li key={index}>User {index + 1}: {user}</li>
              ))}
            </ul>
          ) : (
            <p>No users found</p>
          )}
        </div>
      )}
    </div>
  );
};

export default MainPage;
