const drawTimeline = data => {
  const svg = d3.select(svgRef.current);
  svg.selectAll('*').remove();
  const margin = {
    top: 20,
    right: 15,
    bottom: 30,
    left: 100
  };
  const width = +svg.attr('width') - margin.left - margin.right;
  const height = +svg.attr('height') - margin.top - margin.bottom;

  // Define o domínio para o eixo X
  const allStartTimes = data.flatMap(d => d.times.map(t => t.starting_time));
  const allEndTimes = data.flatMap(d => d.times.map(t => t.ending_time));
  const x = d3.scaleTime().domain([d3.min(allStartTimes),
  // Começo do tempo
  d3.max(allEndTimes) // Fim do tempo
  ]).range([0, width]);
  const y = d3.scaleBand().domain(data.map(d => d.label)).range([0, height]).padding(0.1);
  const xAxis = svg.append('g').attr('class', 'x-axis').attr('transform', `translate(${margin.left},${height + margin.top})`).call(d3.axisBottom(x).tickSizeOuter(0));
  const yAxis = svg.append('g').attr('class', 'y-axis').attr('transform', `translate(${margin.left},${margin.top})`).call(d3.axisLeft(y).tickSize(0).tickPadding(6));
  const barsGroup = svg.append('g').attr('class', 'bars-group').attr('transform', `translate(${margin.left},${margin.top})`);

  // Define a escala de cores
  const colorScale = d3.scaleOrdinal(d3.schemeCategory10);
  const eventsGroup = barsGroup.selectAll('g').data(data).join('g').attr('class', 'event-group').selectAll('rect').data(d => d.times).join('rect').attr('class', 'event-rect').attr('x', d => x(d.starting_time)).attr('y', d => y(data.find(p => p.times.includes(d)).label)).attr('width', d => x(d.ending_time) - x(d.starting_time)).attr('height', y.bandwidth()).attr('fill', (d, i) => {
    if (eventColor && eventColor.length > 0) {
      let color = '#636363';
      data.forEach(event => {
        event.times.forEach(time => {
          if (time === d && event.type === eventColor) {
            color = colorScale(i);
          }
        });
      });
      return color;
    } else {
      return colorScale(i);
    }
  });
  yAxis.selectAll('text').remove();
  yAxis.selectAll('foreignObject').data(data).enter().append('foreignObject').attr('x', -margin.left).attr('y', d => y(d.label)).attr('width', margin.left - 10).attr('height', y.bandwidth()).append('xhtml:div').html(d => `<button class="btn btn-dark d-flex align-items-center justify-content-center" style="width:100%; height:${y.bandwidth()}px; padding-top:10px; padding-bottom:10px;">${d.label}</button>`);
  const zoomBehavior = d3.zoom().scaleExtent([1, 10]).translateExtent([[0, 0], [width, height]]).extent([[0, 0], [width, height]]).on('zoom', event => {
    const newX = event.transform.rescaleX(x);
    xAxis.call(d3.axisBottom(newX).tickSizeOuter(0));
    eventsGroup.attr('x', d => newX(d.starting_time)).attr('width', d => newX(d.ending_time) - newX(d.starting_time));
  });

  // Ajustando a tradução inicial para garantir que o zoom funcione corretamente
  const initialZoom = d3.zoomIdentity.scale(1) // Defina para 1 inicialmente para visualizar todo o gráfico
  .translate(-x(d3.min(allStartTimes)),
  // Mova para a esquerda para o começo do tempo
  0);
  svg.call(zoomBehavior).call(zoomBehavior.transform, initialZoom);
};
