import React, { useEffect, useRef } from 'react'
import * as d3 from 'd3'
import d3Cloud from 'd3-cloud'

const WordCloud = ({ data }) => {
  const svgRef = useRef(null)
  const containerRef = useRef(null)

  useEffect(() => {
    const renderCloud = (containerWidth, containerHeight) => {
      d3.select(svgRef.current).selectAll('*').remove()

      const words = data
        .map((word) => ({ text: word.text, size: word.value }))
        .filter((word) => word.value > 10) // Filtra palavras com valor menor que 10

      const fontFamily = 'sans-serif'

      // Ajustando a escala com base no menor valor entre a largura e a altura do container
      const fontScale = Math.min(containerWidth, containerHeight) / 25
      const maxFontSize = 40 // Tamanho m치ximo da fonte

      const cloud = d3Cloud()
        .size([containerWidth, containerHeight])
        .words(words)
        .padding(5)
        .rotate(() => 0)
        .font(fontFamily)
        .fontSize((d) => Math.min(Math.sqrt(d.size) * fontScale, maxFontSize)) // Limita o tamanho m치ximo da fonte
        .on('end', draw)

      cloud.start()

      function draw(words) {
        const svg = d3.select(svgRef.current)

        svg
          .attr('viewBox', [0, 0, containerWidth, containerHeight])
          .attr('width', containerWidth)
          .attr('height', containerHeight)
          .attr('font-family', fontFamily)
          .attr('text-anchor', 'middle')
          .attr('style', 'max-width: 100%; height: auto;')
          .selectAll('text')
          .data(words)
          .enter()
          .append('text')
          .attr('font-size', (d) => d.size)
          .attr('fill', '#000')
          .attr(
            'transform',
            (d) =>
              `translate(${d.x + containerWidth / 2},${d.y + containerHeight / 2})`
          )
          .text((d) => d.text)
      }
    }

    const resizeObserver = new ResizeObserver((entries) => {
      for (const entry of entries) {
        const { width, height } = entry.contentRect
        renderCloud(width, height)
      }
    })

    if (containerRef.current) {
      resizeObserver.observe(containerRef.current)
    }

    return () => {
      if (containerRef.current) {
        resizeObserver.unobserve(containerRef.current)
      }
    }
  }, [data])

  return (
    <div ref={containerRef} style={{ width: '100%', height: '100%' }}>
      <svg ref={svgRef} style={{ width: '100%', height: '100%' }} />
    </div>
  )
}
