import React, { useEffect } from 'react';
import fetchStaticDataFromAPI from './api/StaticData';
import './App.css';
import BarChart from './components/charts/BarChart';
import ScatterPlot from './components/charts/ScatterPlot';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const fetchedData = await fetchStaticDataFromAPI();
        setData(fetchedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  // Função para contar o número de eventos em cada array 
  const countEvents = (dataArray) => {
    const eventCounts = {};

    dataArray.forEach((events) => {
      events.forEach((event) => {
        const eventType = event.EventType;
        if (eventType in eventCounts) {
          eventCounts[eventType]++;
        } else {
          eventCounts[eventType] = 1;
        }
      });
    });

    return eventCounts;
  };

  const calculateEventDurations = (dataArray) => {
    const eventDurations = {};

    dataArray.forEach((eventsArray) => {
      eventsArray.forEach((event) => {
        const eventType = event.EventType;
        const startTimestamp = new Date(event.Start);
        const endTimestamp = new Date(event.End);
        const duration = endTimestamp - startTimestamp;

        if (eventType in eventDurations) {
          eventDurations[eventType].push(duration);
        } else {
          eventDurations[eventType] = [duration];
        }
      });
    });

    return eventDurations;
  };

  const convertDataForScatterPlot = (time) => {
    const convertedData = [];

    for (const eventType in time) {
      const durations = time[eventType];
      console.log("evento", eventType)
      console.log("Aqui", durations)
      const averageDuration = durations.reduce((acc, curr) => acc + curr, 0) / durations.length;

      // Atribuir a mesma coordenada x para todos os pontos de um tipo de evento
      const xCoordinate = eventType;

      // Usar a média das durações como coordenada y
      const yCoordinate = averageDuration;

      // Adicionar o ponto ao array convertido
      convertedData.push({ x: xCoordinate, y: yCoordinate });
    }

    return convertedData;
  };

  const eventDataCounts = countEvents(data);
  const eventDurations = calculateEventDurations(data);
  const scatterPlotData = convertDataForScatterPlot(eventDurations);

  const eventDataForChart = Object.entries(eventDataCounts).map(([eventType, count]) => ({
    name: eventType,
    value: count,
  }));

  console.log('dados do gráfico:', eventDataForChart);
  console.log('Duração dos eventos:', eventDurations);
  console.log('Duraçãssso dos eventos:', scatterPlotData);

  return (
    <React.Fragment>
      <div>
      <h1>Bar Chart</h1>
      <BarChart data={eventDataForChart} />
    </div>
    <div>
      <h1>Scatter Plot</h1>
      <ScatterPlot data={scatterPlotData} />
    </div>
    </React.Fragment>
  );
}

export default App;
