import React, { useEffect, useState } from 'react'
import '../styles.css'
import { useLocation } from 'react-router-dom'
import { Row, Col, NavItem, NavLink } from 'reactstrap'
import ChooseCharts from '../chooseCharts/ChooseCharts'
import { Nav } from 'react-bootstrap'

const OverviewPage = () => {
  const location = useLocation()
  const [windowWidth, setWindowWidth] = useState(window.innerWidth)
  const [visibleCharts, setVisibleCharts] = useState([])
  const [colSizes, setColSizes] = useState({ col1: 8, col2: 4 })
  const [isSidebarOpen, setIsSidebarOpen] = useState(false)

  const receivedData = location.state?.receivedData || []

  const firstChartsData = [
    {
      title: 'SunBurst',
      activeLabel: 'SunBurst',
      data: receivedData,
      showChart: true,
    },
    {
      title: 'BoxPlot',
      activeLabel: 'BoxPlot',
      data: receivedData,
      showChart: true,
    },
  ]

  const secondChartsData = [
    {
      title: 'SunBurst',
      activeLabel: 'SunBurst',
      data: receivedData,
      showChart: true,
    },
    {
      title: 'BoxPlot',
      activeLabel: 'BoxPlot',
      data: receivedData,
      showChart: true,
    },
    {
      title: 'WordCloud',
      activeLabel: 'WordCloud',
      data: receivedData,
      showChart: true,
    },
  ]

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth)
    window.addEventListener('resize', handleResize)

    const handleMouseMove = (e) => {
      if (e.clientX <= 10) {
        setIsSidebarOpen(true)
      } else if (e.clientX > 250) {
        setIsSidebarOpen(false)
      }
    }

    window.addEventListener('mousemove', handleMouseMove)

    return () => {
      window.removeEventListener('resize', handleResize)
      window.removeEventListener('mousemove', handleMouseMove)
    }
  }, [])

  useEffect(() => {
    if (windowWidth < 900) {
      setVisibleCharts(secondChartsData.slice(0, 2))
      setColSizes({ col1: 6, col2: 6 })
    } else {
      setVisibleCharts(secondChartsData)
      setColSizes({ col1: 8, col2: 4 })
    }
  }, [windowWidth])

  return (
    <div className="content full-height">
      {isSidebarOpen && (
        <Nav vertical>
          <NavItem>
            <NavLink href="#">Dashboard</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">Reports</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">Settings</NavLink>
          </NavItem>
        </Nav>
      )}
      <div className={`main-content ${isSidebarOpen ? 'sidebar-open' : ''}`}>
        <Row className="half-height">
          {firstChartsData.map((chart, index) => {
            let cardClass = ''
            switch (index) {
              case 0:
                cardClass = 'first-card-style-1'
                break
              case 1:
                cardClass = 'first-card-style-2'
                break
              default:
                break
            }

            return (
              <Col
                key={index}
                className={`half-height ${cardClass}`}
                xs={index === 0 ? colSizes.col1 : colSizes.col2}
              >
                <ChooseCharts
                  title={chart.title}
                  activeLabel={chart.activeLabel}
                  data={chart.data}
                  showChart={chart.showChart}
                />
              </Col>
            )
          })}
        </Row>
        <Row className="half-height">
          {visibleCharts.map((chart, index) => {
            let cardClass = ''
            switch (index) {
              case 0:
                cardClass = 'card-style-1'
                break
              case 1:
                cardClass = 'card-style-2'
                break
              case 2:
                cardClass = 'card-style-3'
                break
              default:
                break
            }

            return (
              <Col key={index} className={`half-height ${cardClass}`}>
                <ChooseCharts
                  title={chart.title}
                  activeLabel={chart.activeLabel}
                  data={chart.data}
                  showChart={chart.showChart}
                />
              </Col>
            )
          })}
        </Row>
      </div>
    </div>
  )
}

export default OverviewPage
